Script.script:
reverse_direction_cases = reverse_direction_cases or { ["n"] = "s", ["s"] = "n", ["e"] = "w", ["w"] = "e", ["ne"] = "sw", ["se"] = "nw", ["sw"] = "ne", ["nw"] = "se", ["u"] = "d", ["d"] = "u", ["enter"] = "out", ["in"] = "out", ["out"] = "in", ["climb mountain"] = "climb down" }
function reverseDirection(direction) -- echo("Arg : " .. direction .. "\n") local rev_dir = switch(reverse_direction_cases,direction) --echo("Dir " .. direction .. " reverses to " .. rev_dir .. "\n") if rev_dir == nil then rev_dir = direction end return rev_dir end -- end function
